mapper.insert:
INSERT INTO 
	USER_MST(
		  ID
		, PW
		, NM
		, BIRTH
		, EMAIL
		, TEL
		, SEX
	) VALUES (
		  #{id}
		, #{pw}
		, #{nm}
		, #{birth}
		, #{email}
		, #{tel}
		, #{sex}
	)

mapper.update:
UPDATE USER_MST 
SET EMAIL = #{email}, TEL = #{tel}, SEX = #{sex}, ALLERGY = #{allergy}
, MEDICINE = #{medicine}, DISEASE = #{disease}, PREG_YN = #{pregnant} 
WHERE ID = #{id}

